mapper.select:
SELECT REJI_CD CODE, BNR_NM NAME, BNR_NM_R SHORT_NAME, ACT_KBN KUBUN FROM M005BNRM WHERE (HAKKO_DAY,BMN_CD,REJI_CD,CHU_BNR_CD,YOTO_KBN,SHO_BNR_CD) IN (SELECT MAX(HAKKO_DAY),BMN_CD,REJI_CD,CHU_BNR_CD,YOTO_KBN,SHO_BNR_CD FROM M005BNRM
WHERE HAKKO_DAY <= #{hakkoDay}
AND SUBSTR(BMN_CD, 1, 2) = #{jigyobuCd} AND SUBSTR(BMN_CD, 3, 3) = #{sub_rejiCd} AND REJI_CD = #{rejiCd} AND CHU_BNR_CD = '0000' AND YOTO_KBN = '0000' AND SHO_BNR_CD = '0000' GROUP BY BMN_CD,REJI_CD,CHU_BNR_CD,YOTO_KBN,SHO_BNR_CD) AND DELETE_FLG = '0' ORDER BY BMN_CD

select REJI_CD CODE, BNR_NM NAME, BNR_NM_R SHORT_NAME, ACT_KBN KUBUN from M005BNRM where (BMN_CD, HAKKO_DAY) in ( select BMN_CD, MAX(HAKKO_DAY) from M005BNRM
where HAKKO_DAY <= #{searchDate}
group by BMN_CD ) AND DELETE_FLG = '0'
and SUBSTRB(BMN_CD,1,2) = #{jigyobuCd}
and CHU_BNR_CD = '0000' and YOTO_KBN = '0000' and SHO_BNR_CD = '0000' order by REJI_CD
