mapper.select:
select
distinct
from T000KKKM where (KIKAKU_CD,KAISYA_CD,JIGYOBU_CD,NENDO,
BMN_CD
substr(BMN_CD,1,2),(ACT_KBN||substr(BMN_CD,3,3))
) in ( SELECT KIKAKU_CD,KAISYA_CD,JIGYOBU_CD,NENDO,
BMN_CD
substr(BMN_CD,1,2),min(ACT_KBN||substr(BMN_CD,3,3))
FROM T000KKKM
and BMN_CD= ${jigyobuCd}${bmnCd}
and substr(BMN_CD,1,2)= ${jigyobuCd} group by KIKAKU_CD,KAISYA_CD,JIGYOBU_CD,NENDO, substr(BMN_CD,1,2)
)
order by ${orderByClause}
